import React, { useState } from 'react';
import axios from 'axios';
import jsPDF from 'jspdf';
import './Home.css';

function App() {
  const [e_sal_emp_code, sete_sal_emp_code] = useState('');
  const [employeeDetails, setEmployeeDetails] = useState(null);

  const getEmployeeDetails = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/api/employee/${e_sal_emp_code}`);
      setEmployeeDetails(response.data);
    } catch (error) {
      console.error(error);
      // Handle error
    }
  };

  const downloadDetails = () => {
    if (employeeDetails) {
      const pdf = new jsPDF();
  
      let salaryRows;
      // Set font for the PDF
      pdf.setFont('helvetica');
  
      // Add content to the PDF from the first table (Salary Details)
      const salaryTable = document.querySelector('.tables');
      if (salaryTable) {
        const salaryRows = salaryTable.getElementsByTagName('tr');
        for (let i = 0; i < salaryRows.length; i++) {
          const columns = salaryRows[i].getElementsByTagName('td');
          if (columns.length === 2) {
            // Use larger font size for labels and different colors
            pdf.setTextColor(0, 0, 255); // Blue color for labels
            pdf.setFontSize(14);
            pdf.text(columns[0].innerText, 20, 20 + i * 14);
            pdf.setTextColor(0, 0, 0); // Reset color to black
            pdf.setFontSize(12);
            pdf.text(columns[1].innerText, 60, 20 + i * 14);
          }
        }
      }
  
      // Add content to the PDF from the second table (Gross Details)
      const grossDetailsTable = document.querySelector('.tab1');
      const grossDeductionsTable = document.querySelector('.tab2');
  
      if (grossDetailsTable && grossDeductionsTable) {
        const grossDetailsRows = grossDetailsTable.getElementsByTagName('tr');
        const grossDeductionsRows = grossDeductionsTable.getElementsByTagName('tr');
  
        let yOffset = Math.max(20 + (salaryRows.length || 0) * 14, 40); // Set the Y offset
  
        // Add content from the second table (Gross Details)
        for (let i = 0; i < grossDetailsRows.length; i++) {
          const columns = grossDetailsRows[i].getElementsByTagName('td');
          if (columns.length === 2) {
            // Use larger font size for labels and different colors
            pdf.setTextColor(255, 0, 0); // Red color for labels
            pdf.setFontSize(14);
            pdf.text(columns[0].innerText, 20, yOffset + i * 14);
            pdf.setTextColor(0, 0, 0); // Reset color to black
            pdf.setFontSize(12);
            pdf.text(columns[1].innerText, 60, yOffset + i * 14);
          }
        }
  
        // Add content from the third table (Gross Deductions)
        yOffset = Math.max(yOffset + (grossDetailsRows.length || 0) * 14, 60); // Update the Y offset
        for (let i = 0; i < grossDeductionsRows.length; i++) {
          const columns = grossDeductionsRows[i].getElementsByTagName('td');
          if (columns.length === 2) {
            // Use larger font size for labels and different colors
            pdf.setTextColor(0, 128, 0); // Green color for labels
            pdf.setFontSize(14);
            pdf.text(columns[0].innerText, 20, yOffset + i * 14);
            pdf.setTextColor(0, 0, 0); // Reset color to black
            pdf.setFontSize(12);
            pdf.text(columns[1].innerText, 60, yOffset + i * 14);
          }
        }
      }
  
      // Save the PDF as a blob
      const pdfBlob = pdf.output('blob');
  
      // Create a download link
      const downloadLink = document.createElement('a');
      downloadLink.href = URL.createObjectURL(pdfBlob);
      downloadLink.download = `salary_details_${e_sal_emp_code}.pdf`;
      document.body.appendChild(downloadLink);
      downloadLink.click();
      document.body.removeChild(downloadLink);
    }
  };
  
  return (
    <div className="container">
      <div className="inp">
        <br />
        <div className="divinp">
          <input
            type="text"
            placeholder="Enter Employee Code"
            id="tragss"
            value={e_sal_emp_code}
            onChange={(e) => sete_sal_emp_code(e.target.value)}
          />
          &nbsp;&nbsp;&nbsp;
          <button onClick={getEmployeeDetails}>Get Details</button>
        </div>

        <br />
        {employeeDetails && (
          <div className="sal">
            <div>
              <table className="tables">
                <caption>Salary Details</caption>
                <tbody>
                  <tr>
                    <td>Employee Name:</td>
                    <td>{employeeDetails[19]}</td>
                  </tr>
                  <tr>
                    <td>Employee Code:</td>
                    <td>{employeeDetails[2]}</td>
                  </tr>
                  <tr>
                    <td>Salary Month:</td>
                    <td>{employeeDetails[0]}</td>
                  </tr>
                  <tr>
                    <td>Salary year:</td>
                    <td>{employeeDetails[1]}</td>
                  </tr>
                  <tr>
                    <td>Net salary:</td>
                    <td>
                      <b>{parseFloat(employeeDetails[18]).toFixed(2)}</b>
                    </td>
                  </tr>
                  {/* Add other rows as needed */}
                </tbody>
              </table>

              <br />
              <br />
              <div className="tabs">
                <table className="tab1">
                  <caption>Gross Details</caption>
                  <tbody>
                    <tr>
                      <td>Basic Salary:</td>
                      <td>{employeeDetails[5]}</td>
                    </tr>
                    <tr>
                      <td>Special pay:</td>
                      <td>{employeeDetails[4]}</td>
                    </tr>
                    <tr>
                      <td>Dearness Allowances(DA):</td>
                      <td>{employeeDetails[6]}</td>
                    </tr>
                    <tr>
                      <td>House Rent Allowances(HRA):</td>
                      <td>{employeeDetails[7]}</td>
                    </tr>
                    <tr>
                      <td>Transport Allowance:</td>
                      <td>{employeeDetails[11]}</td>
                    </tr>
                    <tr>
                      <td>Gross Salary:</td>
                      <td>{employeeDetails[8]}</td>
                    </tr>
                    {/* Add other rows as needed */}
                  </tbody>
                </table>

                <table className="tab2">
                  <caption>Gross Deductions</caption>
                  <tbody>
                    <tr>
                      <td>Lic Deduction:</td>
                      <td>{employeeDetails[9]}</td>
                    </tr>
                    <tr>
                      <td>Advance:</td>
                      <td>{employeeDetails[10]}</td>
                    </tr>
                    <tr>
                      <td>Employee State Insurance(ESI):</td>
                      <td>{employeeDetails[12].toFixed(2)}</td>
                    </tr>
                    <tr>
                      <td>Provident Fund(PF):</td>
                      <td>{employeeDetails[15].toFixed(2)}</td>
                    </tr>
                    <tr>
                      <td>Employee Provident Fund (EPF):</td>
                      <td>{employeeDetails[13].toFixed(2)}</td>
                    </tr>
                    <tr>
                      <td>Family Pension Fund:</td>
                      <td>{employeeDetails[14].toFixed(2)}</td>
                    </tr>
                    <tr>
                      <td>Others:</td>
                      <td>{employeeDetails[16]}</td>
                    </tr>
                    <tr>
                      <td>Gross Deductions :</td>
                      <td>{employeeDetails[17].toFixed(2)}</td>
                    </tr>
                    {/* Add other rows as needed */}
                  </tbody>
                </table>
              </div>

              <button id="but" onClick={downloadDetails}>
                Download Details (PDF)
              </button>
              <br />
              <br />
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
