import React, { useState } from "react";
import axios from "axios";
import './Admin.css';

function Admin() {
    const [EmployeeDetails, setEmployeeDetails] = useState([]);
    const [selectedEmployee, setSelectedEmployee] = useState(null);
    const [isPopupOpen, setIsPopupOpen] = useState(false);

    const getEmployeeDetails = async () => {
        try {
            const response = await axios.get(`http://localhost:3001/all_employee_details_Read`);
            setEmployeeDetails(response.data);
        }
        catch (error) {
            console.log(error);
        }
    };

    const openPopup = (employee) => {
        setSelectedEmployee(employee);
        setIsPopupOpen(true);
    };

    const closePopup = () => {
        setIsPopupOpen(false);
    };

    const handleUpdate = async (updatedEmployee) => {
        try {
            // Send updated employee details to backend
            await axios.put(`http://localhost:3001/update_employee/${updatedEmployee.code}`, updatedEmployee);
            // Update table with new employee details
            setEmployeeDetails(EmployeeDetails.map(employee => employee.code === updatedEmployee.code ? updatedEmployee : employee));
            // Close the popup
            closePopup();
        }
        catch (error) {
            console.log(error);
        }
    };

    return (
        <>
            <div>
                <button onClick={getEmployeeDetails}>All Employee details</button>
            </div>
            <h2>My Customers</h2>

            <table id="myTable">
                <thead>
                    <tr className="header">
                        <th>Employee code</th>
                        <th>Name</th>
                        <th>Update</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {EmployeeDetails.map((employee, index) => (
                        <tr key={index}>
                            <td>{employee[0]}</td>
                            <td>{employee.emp_na}</td>
                            <td><button onClick={() => openPopup(employee)}>Update</button></td>
                            <td><button>Delete</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {isPopupOpen && selectedEmployee && (
                <div className="popup">
                    <div className="popup-content">
                        <span className="close" onClick={closePopup}>&times;</span>
                        <h2>Update Employee Details</h2>
                        <form onSubmit={(e) => {
                            e.preventDefault();
                            handleUpdate(selectedEmployee);
                        }}>
                            <label>Employee Code:</label>
                            <input type="text" value={selectedEmployee.code} disabled />
                            <label>Name:</label>
                            <input type="text" value={selectedEmployee.emp_name} onChange={(e) => setSelectedEmployee({...selectedEmployee, emp_name: e.target.value})} />
                            <label>Qualification:</label>
                            <input type="text" value={selectedEmployee.emp_qual} onChange={(e) => setSelectedEmployee({...selectedEmployee, emp_qual: e.target.value})} />
                            {/* Add more input fields for other employee details */}
                            <button type="submit">Update</button>
                        </form>
                    </div>
                </div>
            )}
        </>
    )
}

export default Admin;
