import React, { useState } from 'react';
import axios from 'axios';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import './Home.css';

function App() {
  const [e_sal_emp_code, sete_sal_emp_code] = useState('');
  const [employeeDetails, setEmployeeDetails] = useState(null);

  // Function to fetch employee details from the API
  const getEmployeeDetails = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/api/employee/${e_sal_emp_code}`);
      setEmployeeDetails(response.data);
    } catch (error) {
      console.error(error);
      // Handle error
    }
  };

  // Function to download salary details as PDF
  const downloadDetails = () => {
    if (employeeDetails) {
      const pdf = new jsPDF();
      const startX = 20;
      const startY = 20;
      const lineHeight = 12;

      // Header with "Net Pay Slip"
      pdf.setFont('helvetica', 'bold');
      pdf.setFontSize(18);
      pdf.text('Net Pay Slip', startX, startY);

      // Content rows in a table format
      pdf.setFontSize(14);

      // Create a table for headers with borders
      pdf.autoTable({
        startY: startY + lineHeight,
        theme: 'plain',
        styles: {
          cellPadding: 2,
          fontSize: 12,
          lineWidth: 0.1, // Add border
        },
      });

      // Define table data
      const tableData = [
        // Header row
        [
          { content: 'Employee Name', styles: { fillColor: [0, 102, 204], textColor: [255, 255, 255], fontStyle: 'bold' } },
          { content: 'Employee Code', styles: { fillColor: [0, 102, 204], textColor: [255, 255, 255], fontStyle: 'bold' } },
          { content: 'Salary Month', styles: { fillColor: [0, 102, 204], textColor: [255, 255, 255], fontStyle: 'bold' } },
          { content: 'Gross Deductions', styles: { fillColor: [0, 102, 204], textColor: [255, 255, 255], fontStyle: 'bold' } },
          { content: 'Gross Salary', styles: { fillColor: [0, 102, 204], textColor: [255, 255, 255], fontStyle: 'bold' } },
        ],
        // Data row
        [
          employeeDetails[19], // Employee Name
          employeeDetails[2], // Employee Code
          employeeDetails[0], // Salary Month
          employeeDetails[17].toFixed(2), // Gross Deductions
          employeeDetails[8], // Gross Salary
        ],
      ];

      // Create a table for data with borders
      pdf.autoTable({
        startY: pdf.autoTable.previous.finalY + 5, // Start after the header
        body: tableData,
        theme: 'plain',
        styles: {
          cellPadding: 2,
          fontSize: 12,
          lineWidth: 0.1, // Add border
        },
      });

      // Save the PDF as a blob
      const pdfBlob = pdf.output('blob');

      // Create a download link
      const downloadLink = document.createElement('a');
      downloadLink.href = URL.createObjectURL(pdfBlob);
      downloadLink.download = `salary_details_${e_sal_emp_code}.pdf`;

      document.body.appendChild(downloadLink);
      downloadLink.click();
      document.body.removeChild(downloadLink);
    }
  };

  return (
    <div className="container">
      <div className="inp">
        <br />
        <div className="divinp">
          {/* Input for Employee Code */}
          <input
            type="text"
            placeholder="Enter Employee Code"
            id="tragss"
            value={e_sal_emp_code}
            onChange={(e) => sete_sal_emp_code(e.target.value)}
          />
          &nbsp;&nbsp;&nbsp;
          {/* Button to fetch employee details */}
          <button onClick={getEmployeeDetails}>Get Details</button>
        </div>

        <br />
        {employeeDetails && (
          <div className="sal">
            <div>
              {/* Table for displaying Salary Details */}
              <table className="tables">
                <caption>Salary Details</caption>
                <tbody>
                  {/* Displaying basic details */}
                  <tr>
                    <td>Employee Name:</td>
                    <td>{employeeDetails[19]}</td>
                  </tr>
                  <tr>
                    <td>Employee Code:</td>
                    <td>{employeeDetails[2]}</td>
                  </tr>
                  <tr>
                    <td>Salary Month:</td>
                    <td>{employeeDetails[0]}</td>
                  </tr>
                  <tr>
                    <td>Salary year:</td>
                    <td>{employeeDetails[1]}</td>
                  </tr>
                  <tr>
                    <td>Net salary:</td>
                    <td>
                      <b>{parseFloat(employeeDetails[18]).toFixed(2)}</b>
                    </td>
                  </tr>
                  {/* Add other rows as needed */}
                </tbody>
              </table>

              <br />
              <br />
              {/* Tables for displaying Gross Details and Gross Deductions */}
              <div className="tabs">
                <table className="tab1">
                  <caption>Gross Details</caption>
                  <tbody>
                    {/* Displaying gross details */}
                    <tr>
                      <td>Basic Salary:</td>
                      <td>{employeeDetails[5]}</td>
                    </tr>
                    <tr>
                      <td>Special pay:</td>
                      <td>{employeeDetails[4]}</td>
                    </tr>
                    <tr>
                      <td>Dearness Allowances(DA):</td>
                      <td>{employeeDetails[6]}</td>
                    </tr>
                    <tr>
                      <td>House Rent Allowances(HRA):</td>
                      <td>{employeeDetails[7]}</td>
                    </tr>
                    <tr>
                      <td>Transport Allowance:</td>
                      <td>{employeeDetails[11]}</td>
                    </tr>
                    <tr>
                      <td>Gross Salary:</td>
                      <td>{employeeDetails[8]}</td>
                    </tr>
                    {/* Add other rows as needed */}
                  </tbody>
                </table>

                <table className="tab2">
                  <caption>Gross Deductions</caption>
                  <tbody>
                    {/* Displaying deductions */}
                    <tr>
                      <td>Lic Deduction:</td>
                      <td>{employeeDetails[9]}</td>
                    </tr>
                    <tr>
                      <td>Advance:</td>
                      <td>{employeeDetails[10]}</td>
                    </tr>
                    <tr>
                      <td>Employee State Insurance(ESI):</td>
                      <td>{employeeDetails[12].toFixed(2)}</td>
                    </tr>
                    <tr>
                      <td>Provident Fund(PF):</td>
                      <td>{employeeDetails[15].toFixed(2)}</td>
                    </tr>
                    <tr>
                      <td>Employee Provident Fund (EPF):</td>
                      <td>{employeeDetails[13].toFixed(2)}</td>
                    </tr>
                    <tr>
                      <td>Family Pension Fund:</td>
                      <td>{employeeDetails[14].toFixed(2)}</td>
                    </tr>
                    <tr>
                      <td>Others:</td>
                      <td>{employeeDetails[16]}</td>
                    </tr>
                    <tr>
                      <td>Gross Deductions :</td>
                      <td>{employeeDetails[17].toFixed(2)}</td>
                    </tr>
                    {/* Add other rows as needed */}
                  </tbody>
                </table>
              </div>

              {/* Button to download details as PDF */}
              <button id="but" onClick={downloadDetails}>
                Download Details (PDF)
              </button>
              <br />
              <br />
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
