import "./Login.css"
import {Route, useNavigate} from 'react-router-dom'
import { BrowserRouter, Routes } from 'react-router-dom';
import React, { useState } from 'react';



function Login(){

    const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const loginhandle =() =>{
    if ((name === 'tejasri' && password === '1234') || (name === 'janaki' && password === '1024')) {
        nav('/Admin');
    } else {
        document.getElementById("text").innerHtml = "Invalid username or password";
    }
    
  }
    return(
        <>
        <label htmlFor='wef_date'>UserName</label>
        <input type='text'  value={name} onChange={(e) => setName(e.target.value)}></input>
        <br>
        </br>
        <label htmlFor='wef_date'>Password</label>
        <input type='password'  value={password} onChange={(e) => setPassword(e.target.value)}></input>
        <br>
        </br>
        <input type="submit" onClick={loginhandle} value="Submit"></input>

        </>
    )

}
export default Login;
