import React, { useState } from 'react';
import axios from 'axios';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import './GetYourSalar.css';

function GetYourSalary() {
    const [e_sal_emp_code, sete_sal_emp_code] = useState('');
    const [employeeDetails, setEmployeeDetails] = useState(null);

    const getEmployeeDetails = async () => {
        try {
            const response = await axios.get(`http://localhost:3001/employee/${e_sal_emp_code}`);
            setEmployeeDetails(response.data);
        } catch (error) {
            console.error(error);
            // Handle error
        }
    };

    const downloadDetails = () => {
        if (employeeDetails) {
            const pdf = new jsPDF();
    
            // Heading row centered
            pdf.setFont('helvetica', 'bold');
            pdf.setFontSize(16);
            pdf.text('Salary Details', 20, 20);
    
            // Content rows in a table format
            pdf.setFontSize(14);
    
            // Create a table for headers with background color
            pdf.autoTable({
                startY: 35,
                body: [
                    [{ content: 'Field', styles: { fillColor: [0, 102, 204], textColor: [255, 255, 255], fontStyle: 'bold' } },
                     { content: 'Value', styles: { fillColor: [0, 102, 204], textColor: [255, 255, 255], fontStyle: 'bold' } }],
                ],
                theme: 'plain',
                styles: {
                    cellPadding: 2,
                    fontSize: 12,
                    lineWidth: 0.1, // Add border
                },
            });
    
            // Define table data
            const tableData = [
                ['Employee Name', employeeDetails[19]],
                ['Employee Code', employeeDetails[2]],
                ['Salary Month', employeeDetails[0]],
                ['Basic Salary', employeeDetails[5]],
                ['Basic Salary', employeeDetails[5]],
                ['Special pay', employeeDetails[4]],
                ['Dearness Allowances(DA)', employeeDetails[6]],
                ['House Rent Allowances(HRA)', employeeDetails[7]],
                ['Transport Allowance', employeeDetails[11]],
                ['Gross Salary', employeeDetails[8]],
                ['Lic Deduction', employeeDetails[9]],
                ['Advance', employeeDetails[10]],
                ['Employee State Insurance(ESI)', employeeDetails[12].toFixed(2)],
                ['Provident Fund(PF)', employeeDetails[15].toFixed(2)],
                ['Employee Provident Fund (EPF)', employeeDetails[13].toFixed(2)],
                ['Family Pension Fund', employeeDetails[14].toFixed(2)],
                ['Others', employeeDetails[16]],
                ['Gross Deductions', employeeDetails[17].toFixed(2)],

                // ... (add other rows as needed)
                [{ content: 'Net salary', styles: { fillColor: [255, 255, 0] } },
                 { content: parseFloat(employeeDetails[18]).toFixed(2), styles: { fillColor: [255, 255, 0] } }],
            ];
    
            // Create a table for data with borders
            pdf.autoTable({
                startY: pdf.autoTable.previous.finalY + 5, // Start after the header
                body: tableData,
                theme: 'plain',
                styles: {
                    cellPadding: 2,
                    fontSize: 12,
                    lineWidth: 0.1, // Add border
                },
            });
    
            // Save the PDF as a blob
            const pdfBlob = pdf.output('blob');
    
            // Create a download link
            const downloadLink = document.createElement('a');
            downloadLink.href = URL.createObjectURL(pdfBlob);
            downloadLink.download = `salary_details_${e_sal_emp_code}.pdf`;
            document.body.appendChild(downloadLink);
            downloadLink.click();
            document.body.removeChild(downloadLink);
        }
    };

    return (
        <div className="container">
            <div className="sal">
                <div className="inp">
                    <br />
                    <div className="divinp">
                        <input
                            type="text"
                            placeholder="Enter Employee Code"
                            id="tragss"
                            value={e_sal_emp_code}
                            onChange={(e) => sete_sal_emp_code(e.target.value)}
                        />
                        &nbsp;&nbsp;&nbsp;
                        <button onClick={getEmployeeDetails}>Get Details</button>
                    </div>

                    <br />
                    {employeeDetails && (
                        <div>
                            <h3 align="center">Salary Details</h3>

                            <table className="tables">
                                <tbody>
                                    <tr>
                                        <td>Employee Name:</td>
                                        <td>{employeeDetails[19]}</td>
                                    </tr>
                                    <tr>
                                        <td>Employee Code:</td>
                                        <td>{employeeDetails[2]}</td>
                                    </tr>
                                    <tr>
                                        <td>Salary Month:</td>
                                        <td>{employeeDetails[0]}</td>
                                    </tr>
                                    <tr>
                                        <td>Net salary:</td>
                                        <td>
                                            <b>{parseFloat(employeeDetails[18]).toFixed(2)}</b>
                                        </td>
                                    </tr>
                                    {/* Add other rows as needed */}
                                </tbody>
                            </table>

                            <br />
                            <br />
                            <div className="tabs">
                                <table className="tab1">
                                    <h3 align="center">Gross Details</h3>

                                    <tbody>
                                        {/* <caption>Gross Details</caption> */}
                                        <tr>
                                            <td>Basic Salary:</td>
                                            <td>{employeeDetails[5]}</td>
                                        </tr>
                                        <tr>
