import React, { useState, useEffect } from "react";
import axios from "axios";
import './Admin.css';

function Admin() {
    const [EmployeeDetails, setEmployeeDetails] = useState([]);
    const [selectedEmployee, setSelectedEmployee] = useState(null);
    const [isPopupOpen, setIsPopupOpen] = useState(false);
    const [updatedEmployeeData, setUpdatedEmployeeData] = useState({
        emp_name: "",
        emp_qual: "",
        // Add more fields as needed
    });

    useEffect(() => {
        getEmployeeDetails();
    }, []);

    const getEmployeeDetails = async () => {
        try {
            const response = await axios.get(`http://localhost:3001/all_employee_details_Read`);
            setEmployeeDetails(response.data);
        } catch (error) {
            console.log(error);
        }
    };

    const openPopup = async (employee) => {
        setSelectedEmployee(employee);
        try {
            const response = await axios.get(`http://localhost:3001/employee_details/${employee.c}`);
            setUpdatedEmployeeData(response.data);
            setIsPopupOpen(true);
        } catch (error) {
            console.log(error);
        }
    };

    const closePopup = () => {
        setIsPopupOpen(false);
    };

    const handleUpdate = async () => {
        try {
            await axios.put(`http://localhost:3001/update_employee/${selectedEmployee.code}`, updatedEmployeeData);
            getEmployeeDetails(); // Refresh employee details after update
            setIsPopupOpen(false);
        } catch (error) {
            console.log(error);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUpdatedEmployeeData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };

    return (
        <>
            <div>
                <button onClick={getEmployeeDetails}>All Employee details</button>
            </div>
            <h2>My Customers</h2>

            <table id="myTable">
                <thead>
                    <tr className="header">
                        <th>Employee code</th>
                        <th>Name</th>
                        <th>Update</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {EmployeeDetails.map((employee, index) => (
                        <tr key={index}>
                            <td>{employee[0]}</td>
                            <td>{employee[1]}</td>
                            <td><button onClick={() => openPopup(employee[0])}>Update</button></td>
                            <td><button>Delete</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {isPopupOpen && selectedEmployee && (
                <div className="popup">
                    <div className="popup-content">
                        <span className="close" onClick={closePopup}>&times;</span>
                        <h2>Update Employee Details</h2>
                        <form onSubmit={(e) => {
                            e.preventDefault();
                            handleUpdate();
                        }}>
                            <label>Employee Code:</label>
                            <input type="text" value={selectedEmployee.code} disabled />
                            <label>Name:</label>
                            <input type="text" name="emp_name" value={updatedEmployeeData.emp_name} onChange={handleInputChange} />
                            <label>Qualification:</label>
                            <input type="text" name="emp_qual" value={updatedEmployeeData.emp_qual} onChange={handleInputChange} />
                            {/* Add more input fields for other employee details */}
                            <button type="submit">Update</button>
                        </form>
                    </div>
                </div>
            )}
        </>
    )
}

export default Admin;
