import React, { useState, useEffect } from "react";
import axios from "axios";
import './Admin.css';

function Admin() {
    const [EmployeeDetails, setEmployeeDetails] = useState([]);
    const [selectedEmployee, setSelectedEmployee] = useState(null);
    const [isPopupOpen, setIsPopupOpen] = useState(false);
    const [updatedEmployeeData, setUpdatedEmployeeData] = useState({
        emp_code: "",
        emp_name: "",
        emp_qual: "",
        emp_join_date: "",
        emp_resignation_date: "",
        emp_pan_no: "",
        emp_aadhar_no: "",
        emp_pf_flag: "",
        emp_pf_no: "",
        emp_esi_no: "",
        emp_est_flag: "",
        emp_gst_no: "",
        emp_basic: "",
        emp_dept: "",
        emp_hno: "",
        emp_street: "",
        emp_city: "",
        emp_pincode: "",
        emp_state: "",
        emp_ph_no: "",
        emp_email_id: "",
        emp_cons_res_flag: "",
        emp_spl_pay: "",
        emp_bank_ifsc: "",
        emp_bank_name: "",
        emp_bank_account_no: ""
    });

    useEffect(() => {
        getEmployeeDetails();
    }, []);

    const getEmployeeDetails = async () => {
        try {
            const response = await axios.get(`http://localhost:3001/all_employee_details_Read`);
            setEmployeeDetails(response.data);
        } catch (error) {
            console.log(error);
        }
    };

    const openPopup = async (employee) => {
        setSelectedEmployee(employee);
        try {
            const response = await axios.get(`http://localhost:3001/employee_detail_Read/${employee[0]}`);
            setUpdatedEmployeeData(response.data);
            setIsPopupOpen(true);
        } catch (error) {
            console.log(error);
        }
    };

    const closePopup = () => {
        setIsPopupOpen(false);
    };

    const handleUpdate = async () => {
        try {
            await axios.put(`http://localhost:3001/update_employee/${selectedEmployee[0]}`, updatedEmployeeData);
            getEmployeeDetails();
            setIsPopupOpen(false);
        } catch (error) {
            console.log(error);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUpdatedEmployeeData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };

    return (
        <>
            <div>
                <button onClick={getEmployeeDetails}>All Employee details</button>
            </div>
            <h2>My Customers</h2>

            <table id="myTable">
                <thead>
                    <tr className="header">
                        <th>Employee code</th>
                        <th>Name</th>
                        <th>Update</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {EmployeeDetails.map((employee, index) => (
                        <tr key={index}>
                            <td>{employee[0]}</td>
                            <td>{employee[1]}</td>
                            <td><button onClick={() => openPopup(employee)}>Update</button></td>
                            <td><button>Delete</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {isPopupOpen && selectedEmployee && (
                <div className="popup">
                    <div className="popup-content">
                        <span className="close" onClick={closePopup}>&times;</span>
                        <h2>Update Employee Details</h2>
                        <form onSubmit={(e) => {
                            e.preventDefault();
                            handleUpdate();
                        }}>
                            <label>Employee Code:</label>
                            <input type="text" name="emp_code" value={updatedEmployeeData.emp_code} disabled />
                            <label>Name:</label>
                            <input type="text" name="emp_name" value={updatedEmployeeData.emp_name} onChange={handleInputChange} />
                            <label>Qualification:</label>
                            <input type="text" name="emp_qual" value={updatedEmployeeData.emp_qual} onChange={handleInputChange} />
                            {/* Add more input fields for other employee details */}
                            <button type="submit">Update</button>
                        </form>
                    </div>
                </div>
            )}
        </>
    )
}

export default Admin;
