import React, { useState } from 'react';
import axios from 'axios';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import './Home.css';

function App() {
  const [e_sal_emp_code, sete_sal_emp_code] = useState('');
  const [employeeDetails, setEmployeeDetails] = useState(null);

  // Function to fetch employee details from the API
  const getEmployeeDetails = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/api/employee/${e_sal_emp_code}`);
      setEmployeeDetails(response.data);
    } catch (error) {
      console.error(error);
      // Handle error
    }
  };

  // Function to download salary details as PDF
  const downloadDetails = () => {
    if (employeeDetails) {
      const pdf = new jsPDF();
      const startX = 20;
      const startY = 20;
      const lineHeight = 12;

      // Header with "Net Pay Slip"
      pdf.setFont('helvetica', 'bold');
      pdf.setFontSize(18);
      pdf.text('Net Pay Slip', startX, startY);

      // Employee Name, Employee Code, and Salary Month
      pdf.setFontSize(14);
      pdf.text(`Employee Name: ${employeeDetails[19]}`, startX, startY + lineHeight);
      pdf.text(`Employee Code: ${employeeDetails[2]}`, startX, startY + 2 * lineHeight);
      pdf.text(`Salary Month: ${employeeDetails[0]}`, startX, startY + 3 * lineHeight);

      // Section separator line
      pdf.setLineWidth(0.5);
      pdf.line(startX, startY + 4 * lineHeight, 190, startY + 4 * lineHeight);

      // Gross Details
      pdf.setFont('helvetica', 'bold');
      pdf.setFontSize(14);
      pdf.text('Gross Details', startX, startY + 4.5 * lineHeight);

      const grossDetails = [
        ['Basic Salary', employeeDetails[5]],
        ['Special pay', employeeDetails[4]],
        ['Dearness Allowances(DA)', employeeDetails[6]],
        ['House Rent Allowances(HRA)', employeeDetails[7]],
        ['Transport Allowance', employeeDetails[11]],
        ['Gross Salary', employeeDetails[8]],
      ];

      pdf.autoTable({
        startY: startY + 5.5 * lineHeight,
        body: grossDetails,
        theme: 'plain',
        styles: {
          cellPadding: 2,
          fontSize: 12,
          lineWidth: 0.1,
        },
      });

      // Section separator line
      pdf.setLineWidth(0.5);
      pdf.line(startX, pdf.autoTable.previous.finalY + 5, 190, pdf.autoTable.previous.finalY + 5);

      // Gross Deductions
      pdf.setFont('helvetica', 'bold');
      pdf.setFontSize(14);
      pdf.text('Gross Deductions', startX, pdf.autoTable.previous.finalY + 5.5 * lineHeight);

      const grossDeductions = [
        ['Lic Deduction', employeeDetails[9]],
        ['Advance', employeeDetails[10]],
        ['Employee State Insurance(ESI)', employeeDetails[12].toFixed(2)],
        ['Provident Fund(PF)', employeeDetails[15].toFixed(2)],
        ['Employee Provident Fund (EPF)', employeeDetails[13].toFixed(2)],
        ['Family Pension Fund', employeeDetails[14].toFixed(2)],
        ['Others', employeeDetails[16]],
        ['Gross Deductions', employeeDetails[17].toFixed(2)],
      ];

      pdf.autoTable({
        startY: pdf.autoTable.previous.finalY + 5,
        body: grossDeductions,
        theme: 'plain',
        styles: {
          cellPadding: 2,
          fontSize: 12,
          lineWidth: 0.1,
        },
      });

      // Section separator line
      pdf.setLineWidth(0.5);
      pdf.line(startX, pdf.autoTable.previous.finalY + 5, 190, pdf.autoTable.previous.finalY + 5);

      // Net Salary (highlighted)
      pdf.setFont('helvetica', 'bold');
      pdf.setFontSize(14);
      pdf.text('Net Salary', startX, pdf.autoTable.previous.finalY + 5.5 * lineHeight, { fillColor: [255, 255, 0] });
      pdf.text(`${parseFloat(employeeDetails[18]).toFixed(2)}`, startX + 100, pdf.autoTable.previous.finalY + 5.5 * lineHeight, { fillColor: [255, 255, 0] });

      // Save the PDF as a blob
      const pdfBlob = pdf.output('blob');

      // Create a download link
      const downloadLink = document.createElement('a');
      downloadLink.href = URL.createObjectURL(pdfBlob);
      downloadLink.download = `salary_details_${e_sal_emp_code}.pdf`;

      document.body.appendChild(downloadLink);
      downloadLink.click();
      document.body.removeChild(downloadLink);
    }
  };

  return (
    <div className="container">
      <div className="inp">
        <br />
        <div className="divinp">
          {/* Input for Employee Code */}
          <input
            type="text"
            placeholder="Enter Employee Code"
            id="tragss"
            value={e_sal_emp_code}
            onChange={(e) => sete_sal_emp_code(e.target.value)}
          />
          &nbsp;&nbsp;&nbsp;
          {/* Button to fetch employee details */}
          <button onClick={getEmployeeDetails}>Get Details</button>
        </div>

        <br />
        {employeeDetails && (
          <div className="sal">
            <div>
              {/* Displaying basic details */}
              <table className="tables">
                <caption>Salary Details</caption>
                <tbody>
                  <tr>
                    <td>Employee Name:</td>
                    <td>{employeeDetails[19]}</td>
                  </tr>
                  <tr>
                    <td>Employee Code:</td>
                    <td>{employeeDetails[2]}</td>
                  </tr>
                  <tr>
                    <td>Salary Month:</td>
                    <td>{employeeDetails[0]}</td>
                  </tr>
                  <tr>
                    <td>Salary year:</td>
                    <td>{employeeDetails[1]}</td>
                  </tr>
                  {/* Add other rows as needed */}
                </tbody>
              </table>

              <br />
              <br />
              {/* Tables for displaying Gross Details and Gross Deductions */}
              <div className="tabs">
                {/* Table for Gross Details */}
                <table className="tab1">
                  <caption>Gross Details</caption>
                  <tbody>
                    <tr>
                      <td>Basic Salary:</td>
                      <td>{employeeDetails[5]}</td>
                    </tr>
                    <tr>
                      <td>Special pay:</td>
                      <td>{employeeDetails[4]}</td>
                    </tr>
                    <tr>
                      <td>Dearness Allowances(DA):</td>
                      <td>{employeeDetails[6]}</td>
                    </tr>
                    <tr>
                      <td>House Rent Allowances(HRA):</td>
                      <td>{employeeDetails[7]}</td>
                    </tr>
                    <tr>
                      <td>Transport Allowance:</td>
                      <td>{employeeDetails[11]}</td>
                    </tr>
                    <tr>
                      <td>Gross Salary:</td>
                      <td>{employeeDetails[8]}</td>
                    </tr>
                    {/* Add other rows as needed */}
                  </tbody>
                </table>

                {/* Table for Gross Deductions */}
                <table className="tab2">
                  <caption>Gross Deductions</caption>
                  <tbody>
                    <tr>
                      <td>Lic Deduction:</td>
                      <td>{employeeDetails[9]}</td>
                    </tr>
                    <tr>
                      <td>Advance:</td>
                      <td>{employeeDetails[10]}</td>
                    </tr>
                    <tr>
                      <td>Employee State Insurance(ESI):</td>
                      <td>{employeeDetails[12].toFixed(2)}</td>
                    </tr>
                    <tr>
                      <td>Provident Fund(PF):</td>
                      <td>{employeeDetails[15].toFixed(2)}</td>
                    </tr>
                    <tr>
                      <td>Employee Provident Fund (EPF):</td>
                      <td>{employeeDetails[13].toFixed(2)}</td>
                    </tr>
                    <tr>
                      <td>Family Pension Fund:</td>
                      <td>{employeeDetails[14].toFixed(2)}</td>
                    </tr>
                    <tr>
                      <td>Others:</td>
                      <td>{employeeDetails[16]}</td>
                    </tr>
                    <tr>
                      <td>Gross Deductions :</td>
                      <td>{employeeDetails[17].toFixed(2)}</td>
                    </tr>
                    {/* Add other rows as needed */}
                  </tbody>
                </table>
              </div>

              {/* Button to download details as PDF */}
              <button id="but" onClick={downloadDetails}>
                Download Details (PDF)
              </button>
              <br />
              <br />
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
